SELECT T1.*,T2.* FROM ( SELECT B.rnum, B.employee_id, B.password, B.name, B.dept_no, (select dept_nm from dept where dept_no = B.dept_no)as dept_nm, B.position_no, (select position_nm from position where position_no = B.position_no)as position_nm, B.cell_phone, B.email, B.address, TO_CHAR(B.hire_date,'YYYYMMDD') hire_date, B.birth_day, B.holiday, B.img_code FROM ( SELECT ROWNUM rnum, A.* FROM( SELECT * FROM employee_view
ORDER BY hire_date DESC )A WHERE rownum <= ( #{pageSize} * ( #{pageNum} - 1 ) + #{pageSize}) )B WHERE rnum >= ( #{pageSize} * ( #{pageNum} - 1 ) + 1 ) )T1 CROSS JOIN --전체COUNT (SELECT COUNT(*) totalCnt FROM employee_view
)T2

SELECT count(*) as cnt 
FROM employee       
WHERE employee_id= #{employee_id}
    AND password=#{password}

SELECT count(*) as cnt      
FROM employee            
WHERE employee_id=#{employee_id}
